*/ namespace Module\Member\Admin\Controller; use Illuminate\Routing\Controller; use ModStart\Admin\Concern\HasAdminQuickCRUD; use ModStart\Admin\Layout\AdminCRUDBuilder; use ModStart\Core\Dao\ModelUtil; use ModStart\Form\Form; use ModStart\Grid\GridFilter; use ModStart\Support\Concern\HasFields; use Module\Member\Biz\Vip\MemberVipBiz; use Module\Member\Util\MemberVipUtil; class MemberVipSetController extends Controller { use HasAdminQuickCRUD; protected function crud(AdminCRUDBuilder $bhaAK) { $bhaAK->init('member_vip_set')->field(function ($bhaAK) { $bhaAK->id('id', 'ID')->addable(true)->editable(true)->ruleUnique('member_vip_set')->required()->defaultValue(ModelUtil::sortNext('member_vip_set', array(), 'id')); $bhaAK->text('title', '名称')->required()->ruleUnique('member_vip_set'); $bhaAK->text('flag', '英文标识')->required()->ruleUnique('member_vip_set'); $bhaAK->switch('isDefault', '默认')->optionsYesNo()->help('会员是否默认为该等级')->required(); $bhaAK->image('icon', '图标'); $bhaAK->currency('price', '价格')->required(); $bhaAK->number('vipDays', '时间')->required()->help('单位为天，365表示1年'); foreach (MemberVipBiz::all() as $HLJws) { $HLJws->vipField($bhaAK); } $bhaAK->richHtml('content', '说明')->required(); $bhaAK->display('created_at', L('Created At'))->listable(false); $bhaAK->display('updated_at', L('Updated At'))->listable(false); })->gridFilter(function (GridFilter $P2EU9) { $P2EU9->like('title', '名称'); })->enablePagination(false)->defaultOrder(array('sort', 'asc'))->canSort(true)->title('VIP等级')->addDialogSize(array('600px', '95%'))->editDialogSize(array('600px', '95%'))->hookSaved(function (Form $mUGak) { MemberVipUtil::clearCache(); }); } }